INSERT INTO `mmc_faq` (`faq_id`, `language_id`, `title`, `text`, `status`, `is_top`, `sort_order`, `date_added`, `user_id`) VALUES
(19, 1, 'Can I cancel my VIP membership and get my refund?', '&lt;p&gt;Yes, you can cancel it at any time, and 100% money back guarantee for your VIP membership balance without any condiction. But it doesn''t inculde the payment of your previous orders and the free bonus you''ve got from us.&lt;/p&gt;\n', 1, 0, 0, '2013-11-05 21:38:16', 0),
(20, 1, ' Is there any deadline for my VIP membership? ', '&lt;p&gt;No, you will our permanent VIP member. But if you want to get discount for the service, you''d better load money to your balance to enjoy the discount before you purchase anything.&lt;/p&gt;\r\n', 1, 0, 0, '2013-11-05 21:41:03', 0),
(21, 1, ' Can the VIP discount be available for other special offers? ', '&lt;p&gt;No,it can''t. You can get the VIP discount for any product in our sales page. However, it can not be applied to our other special offers.&lt;/p&gt;\r\n', 1, 0, 0, '2013-11-05 21:42:38', 0),
(22, 1, 'How can I upgrade to higher level VIP membership?', '&lt;p&gt;If you want to upgrade to higher VIP levels, you can click load button beside My Balance section and choose which level you want to upgrade to and then go on to load money to your balance.&lt;/p&gt;\r\n', 1, 0, 0, '2013-11-05 21:44:50', 0),
(23, 1, 'How can I get my balance information of VIP program? ', '&lt;p&gt;If there is any change of your balance, you will get informed by receiving an email from us. The email will be sent to you automatically by the system.&lt;/p&gt;\r\n', 1, 0, 0, '2013-11-05 22:23:06', 0),
(24, 1, 'How to use the balance in my VIP Program to place orders? ', '&lt;p&gt;When you are to buy any service from us by using your balance, you just need to login your VIP account and choose the product you want to buy and then go to check out with your balance directly. And your order will be processed by us soon.&lt;/p&gt;\r\n', 1, 0, 0, '2013-11-05 22:25:00', 0),
(25, 1, 'How to join the VIP Program?', '&lt;p&gt;If you want to join our VIP membership, please contact our 24*7 support for help. And we will guide you how to join the VIP membership.&lt;/p&gt;\r\n', 1, 0, 0, '2013-11-05 22:26:23', 0),
(26, 1, 'Do you charge any fee for the VIP Membership?', '&lt;p&gt;No, it''s totally free for you to join the VIP membership. You can use all the balance in your VIP balance to buy any product or service in the VIP Member area.&lt;/p&gt;\r\n', 1, 0, 0, '2013-11-05 22:27:59', 0),
(27, 1, 'How much discount will I get as VIP member?', '&lt;p&gt;You will get discount for any product in the VIP member area based on your VIP level. And the discount will be calculated automatically when you choose any product.&lt;/p&gt;\r\n', 1, 0, 0, '2013-11-06 20:14:56', 0),
(28, 1, 'Can I get more discount for the product if I want to buy any product in bulk quantity?', '&lt;p&gt;Sorry, if you want to get more discount for any product, you can upgrade your VIP level to the top level which is Diamond VIP. And then you will get 50% discount for any product. 50% discount is the best discount we can offer now.&lt;/p&gt;\r\n', 1, 0, 0, '2013-11-06 20:39:15', 0),
(29, 1, 'Can I cancel any order when I don''t want it?', '&lt;p&gt;Yes. You can cancel the order only if the project status is &quot;Paid&quot;. If it is changed, I''m sorry you can''t cancel it as the project is already being processed.&lt;/p&gt;\r\n', 1, 0, 0, '2013-11-06 20:39:45', 0),
(30, 1, 'How can I cancel my order?', '&lt;p&gt;Please make sure the project status is &quot;Paid&quot; and then contact our 24*7 support team to cancel your order.&lt;/p&gt;\r\n', 1, 0, 0, '2013-11-06 20:40:28', 0),
(31, 1, 'How can I check my order status?', '&lt;p&gt;Please click &quot;My Account&quot; on the top left of any VIP page and go to &quot;Order History&quot; tab to click View button to check your order status.&lt;/p&gt;\r\n', 1, 0, 0, '2013-11-06 20:40:59', 0),
(32, 1, 'What''s Lively 24*7 Service VIP Program?', '&lt;p&gt;Lively 24*7 Service is not open to the public any longer. And only our customers who contact our 24*7 support team to apply for joining the VIP membership . Only our VIP members can enjoy our service in the VIP member area and get discounts based on different VIP levels.&lt;/p&gt;\r\n', 1, 1, 1, '2013-11-06 20:52:41', 0);
